const buildTypes = (a) => {
  // creating states for the type of build being gauged
  //
  // "smoke",  "regression" of a full build of no tags at all
  if (!a) {
    return 1;
  } else if (a.split(",").map((tag) => tag.trim()).indexOf("regression") >= 0) {
    return 2;
  } else if (a.split(",").map((tag) => tag.trim()).indexOf("smoke") >= 0) {
    return 3;
  }
  return 1;
};

module.exports = buildTypes;
