"use strict";

const runPathBuilder = (suffix, jobName) => {
  const path = ["magellan", "suiteRun"];
  jobName = jobName || process.env.MAGELLAN_JOB_NAME;

  if (jobName && jobName.length > 0) {
    const jobPath = jobName.split(".");
    jobPath.forEach((token) => {
      if (token.trim().length > 0) {
        path.push(token.trim());
      }
    });
  }

  path.push(suffix);

  return path.join(".");
};

module.exports = runPathBuilder;
