/* eslint import/no-extraneous-dependencies: 0, no-undef: 0 */

const expect = require("chai").expect;
const buildType = require("../src/build-type");

describe("buildType", () => {
  it("should return the correct build type for smoke", () => {
    expect(buildType("smoke")).to.eql(3);
  });

  it("should return the correct build type for regression", () => {
    expect(buildType("regression")).to.eql(2);
  });

  it("should return the correct build type without tags", () => {
    expect(buildType()).to.eql(1);
    expect(buildType("")).to.eql(1);
    expect(buildType("blah")).to.eql(1);
  });
});
