const expect = require("chai").expect;
const getTags = require("../src/get-tags");

describe("getTags", () => {
  it("should find a tag in a file", () => {
    const filename = "test/helpers//test.js";
    const output = getTags(filename);
    expect(output[0]).to.equal("oogabooga");
  });
});
