/* eslint import/no-extraneous-dependencies: 0, no-undef: 0 */

const expect = require("chai").expect;
const runPathBuilder = require("../src/run-path-builder");

describe("runPathBuilder", () => {
  it("should construct the correct path for an empty jobname", () => {
    expect(runPathBuilder("a")).to.equal("magellan.suiteRun.a");
    expect(runPathBuilder("a", "b.c")).to.equal("magellan.suiteRun.b.c.a");
    expect(runPathBuilder("a", "b..c")).to.equal("magellan.suiteRun.b.c.a");
  });
});
