import "babel-polyfill";

const j = () => new Promise((resolve, reject) => {
    resolve(1);
});

const h = () => new Promise((resolve, reject) => {
    resolve(2);
});

const k = async function (input) {
    const p1 = await j();
    const p2 = await h();
    // return input + p1 + p2
    return () => new Promise(resolve => resolve(input + p1 + p2));
};

// const p = async () => await k(10);

k(10).then(l => l().then(m => console.log(m)));