"use strict";

class AttemptList {
  constructor(attempts) {
    this.attempts = attempts;
    this.passed = false;
    this.failed = true;
    for (const att of this.attempts) {
      if (att.passed) {
        this.passed = true;
        this.failed = false;
      }
    }
    this.retries = this.attempts.length - (this.passed ? 1 : 0);
  }
}

module.exports = AttemptList;
