"use strict";

class MarkedContainer {
  constructor() {
    this.markers = [];
  }

  addMarker(marker) {
    this.markers.push(marker);
    this.markers = this.markers.sort((a, b) => {
      if (a.t < b.t) {
        return -1;
      } else if (a.t > b.t) {
        return 1;
      } else {
        return 0;
      }
    });
  }

  getMarker(name) {
    for (const mkr of this.markers) {
      if (mkr.name === name) {
        return mkr;
      }
    }
    return null;
  }

  get timeElapsed() {
    if (this.markers.length > 1) {
      return this.markers[this.markers.length - 1].t - this.markers[0].t;
    }
    return 0;
  }
}

module.exports = MarkedContainer;
