"use strict";

const MarkedContainer = require("./marked-container");

class TestRun extends MarkedContainer {
  constructor(test, environment, attemptNumber) {
    super();
    this.test = test;
    this.environment = environment;
    this.status = null;
    this.passed = null;
    this.metadata = {};
    this.attempt = false;
    this.attemptNumber = attemptNumber;
  }
}

module.exports = TestRun;
