"use strict";

const MarkedContainer = require("./marked-container");

class Worker extends MarkedContainer {
  constructor(id) {
    super();
    this.id = id;
    this.state = null;
  }

  get satisfied() {
    return this.getMarker("end") !== null;
  }

  get failed() {
    return this.getMarker("failed") !== null;
  }
}

module.exports = Worker;
