/* eslint import/no-extraneous-dependencies: 0, no-undef: 0 */

const expect = require("chai").expect;
const TestRun = require("../../src/srr/test-run");

describe("TestRun", () => {
  it("handle props", () => {
    const wkr = new TestRun({name: "foo"}, "bar", 1);
    expect(wkr.test.name).to.equal("foo");
    expect(wkr.environment).to.equal("bar");
    expect(wkr.attemptNumber).to.equal(1);
  });
});
