/* eslint import/no-extraneous-dependencies: 0, no-undef: 0 */

const expect = require("chai").expect;
const Worker = require("../../src/srr/worker");

describe("Worker", () => {
  it("handle props", () => {
    const wkr = new Worker("foo");
    expect(wkr.id).to.equal("foo");
    wkr.state = "bar";
    expect(wkr.state).to.equal("bar");
  });

  it("handle props", () => {
    const wkr = new Worker("foo");
    wkr.addMarker({t: 0, name: "start"});
    wkr.addMarker({t: 1000, name: "end"});
    expect(wkr.timeElapsed).to.equal(1000);
  });
});
